import React, {Component} from 'react';
import { connect } from 'react-redux'

class Cart extends Component {
    render() {
        let addedItems = this.props.items.length ?
            (
                this.props.items.map(item=>{
                    return(
                        <>
                            <ul className={"row ml-0"} key={item.id}>
                                <li className={"col-6 list-group-item"}>
                                    <h4>{item.name}</h4>
                                    <p><span className={"mr-3 font-weight-bold"}>MRP: {item.price}</span>
                                        <button onClick={()=>{this.handleSubtractQuantity(item.id)}} className={"btn btn-warning mr-2"}>-</button>
                                        {item.quantity}
                                        <button onClick={()=>{this.handleAddQuantity(item.id)}} className={"btn btn-success ml-2"}>+</button>
                                    </p>
                                </li>
                                <li className={"col-3 list-group-item d-flex justify-content-center align-items-center"}>
                                    {item.price * item.quantity}
                                </li>
                                <li className={"col-3 list-group-item d-flex justify-content-center align-items-center"}>
                                    <button className="btn btn-danger" onClick={()=>{this.handleRemove(item.id)}}>Remove</button>
                                </li>
                            </ul>
                        </>
                    )
                })
            ):

            (
                <p>Nothing.</p>
            );
        let totalItems = this.props.total;

        return(
            <div className="container">
                <div className="cart">
                    <h5>Your Cart:</h5>
                    <ul className="collection">
                        {addedItems}
                        <ul className={"row ml-0"   }>
                            <li className={"col-6 list-group-item"}>
                                <h3>Grand Total</h3>
                            </li>
                            <li className={"col-3 list-group-item d-flex justify-content-center align-items-center"}>
                                <h3>{totalItems}</h3>
                            </li>
                        </ul>
                    </ul>
                </div>
            </div>
        )
    }
}


const mapStateToProps = (state)=>{
    return{
        items: state.addedItems,
        addedItems: state.addedItems,
        total: state.total,
        totalItem: state.totalItem
    }
}
const mapDispatchToProps = (dispatch)=>{
    return{
        // removeItem: (id)=>{dispatch(removeItem(id))},
        // addQuantity: (id)=>{dispatch(addQuantity(id))},
        // subtractQuantity: (id)=>{dispatch(subtractQuantity(id))}
    }
}
export default connect(mapStateToProps,mapDispatchToProps)(Cart)
