import products from "../../data";
import { ADD_TO_CART } from "../action/cartAction"

const initstate={
    items:products,
    addedItems:[],
    total:0
}

const cartReducer=(state=initstate,action)=>{
    switch (action.type) {
        default: return state;

    }
}

export default cartReducer;
